mapper.update:
update company set 
cname = #{cname}, cbuild= #{cbuild}, ccategory= #{ccategory}, ccompanyFile= #{ccompanyFile},
cstaff = #{cstaff}, csales = #{csales}, ccontent = #{ccontent},cpw=#{cpw}
where cid = #{cid}

update company set
ctype = #{ctype}, cdate = #{cdate}
where cid = #{cid}

update company set
ctype = #{ctype}
where cid = #{cid}

update company set
ccompanyFile = null
where cid = #{cid} and cpw=#{cpw}

update applicant set
apcancel = 1
where ano = #{ano}
